package All_loops;

public class ContinueKeyword {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("print all even number"); 
        for(int i=2; i<100; i++) {
        if(i%2==1) {
        	continue;
        	
        }
          System.out.println(i); 
	    }
        
	}

}